select d_year, s_city, p_brand, sum(lo_revenue - lo_supplycost) as profit from lineorder JOIN supplier ON (lo_suppkey = s_suppkey) JOIN dates ON (lo_orderdate = d_datekey) JOIN part ON (lo_partkey = p_partkey) JOIN customer ON (lo_custkey = c_custkey) where s_nation = 'UNITED STATES' and (d_year = 1997 or d_year = 1998) and p_category = 'MFGR#14' group by d_year, s_city, p_brand order by d_year, s_city, p_brand;
