select d_year, c_nation, sum(lo_revenue - lo_supplycost) as profit from dates JOIN (part JOIN (customer JOIN (supplier JOIN lineorder ON (s_suppkey = lo_suppkey)) ON (c_custkey=lo_custkey)) ON (p_partkey=lo_partkey)) ON (d_datekey=lo_orderdate) where c_region = 'AMERICA' and s_region = 'AMERICA' and (p_mfgr = 'MFGR#1' or p_mfgr = 'MFGR#2') group by d_year, c_nation order by d_year, c_nation;
