select d_year, s_nation, p_category, sum(lo_revenue - lo_supplycost) as profit from customer JOIN ( part JOIN (dates JOIN (supplier JOIN lineorder ON (s_suppkey = lo_suppkey)) ON (d_datekey=lo_orderdate)) ON (p_partkey=lo_partkey)) ON (c_custkey=lo_custkey) where c_region = 'AMERICA' and s_region = 'AMERICA' and (d_year = 1997 or d_year = 1998) and (p_mfgr = 'MFGR#1' or p_mfgr = 'MFGR#2') group by d_year, s_nation, p_category order by d_year, s_nation, p_category;
