select d_year, s_city, p_brand, sum(lo_revenue - lo_supplycost) as profit from customer JOIN ( part JOIN (dates JOIN (supplier JOIN lineorder ON (s_suppkey = lo_suppkey)) ON (d_datekey=lo_orderdate)) ON (p_partkey=lo_partkey)) ON (c_custkey=lo_custkey) where s_nation = 'UNITED STATES' and (d_year = 1997 or d_year = 1998) and p_category = 'MFGR#14' group by d_year, s_city, p_brand order by d_year, s_city, p_brand;
