select c_custkey, c_name, c_acctbal, c_phone, n_name, c_address, c_comment, sum(l_extendedprice * (1 - l_discount)) as revenue from customer JOIN orders ON (c_custkey = o_custkey) JOIN nation ON (c_nationkey = n_nationkey) JOIN lineitem ON (o_orderkey = l_orderkey) where o_orderdate >= '1993-10-01' and o_orderdate < '1994-01-01' and l_returnflag = 'R' group by c_custkey, c_name, c_acctbal, c_phone, n_name, c_address, c_comment order by revenue desc limit 20;
